mapper.select:
  select t11.*
  			from (select t1.*, rownum rn
	          from (
	                select m.*,u.user_name as priceConfigAuditUserName from t_migu_price_config m,t_migu_users u where m.price_config_audit_user = u.user_id and  m.PROJECT_ID= #{projectId,jdbcType=VARCHAR} ORDER BY SUBMIT_AUDIT_TIME DESC
	                ) t1) t11
	 	where rn between (#{page} - 1) * #{pageSize} + 1 and
	       (#{page}) * #{pageSize}

mapper.update:

update T_MIGU_PRICE_CONFIG
set SUBMIT_AUDIT_TIME = #{submitAuditTime,jdbcType=VARCHAR},
  PRICE_CONFIG_AUDIT_USER = #{priceConfigAuditUser,jdbcType=VARCHAR},
  PRICE_CONFIG_AUDIT_RESULT = #{priceConfigAuditResult,jdbcType=VARCHAR}
where PRICE_CONFIG_ID = #{priceConfigId,jdbcType=DECIMAL}
