mapper.select:
select i.project_id, i.product_id, i.product_name, i.vendor_id, i.vendor_name, i.purchase_price, i.min_sell_price, i.offer_start_time, i.offer_end_time, i.illustrate, i.PRICE_CONFIG_INFO_ID from t_migu_price_config_info i where 1=1
and i.product_id =#{productId,jdbcType=VARCHAR}
and i.offer_start_time = #{offerStartTime,jdbcType=VARCHAR}
and i.offer_end_time = #{offerEndTime,jdbcType=VARCHAR}
and i.project_id =#{projectId,jdbcType=VARCHAR}

select t11.* from (select t1.*, rownum rn from (SELECT i.project_id, i.product_id, i.product_name, i.vendor_id, i.vendor_name, i.purchase_price, i.min_sell_price, i.offer_start_time, i.offer_end_time, i.illustrate, i.PRICE_CONFIG_INFO_ID FROM t_migu_price_config_info i where 1=1
and i.project_id = #{projectId,jdbcType=VARCHAR}
AND (i.PRODUCT_ID like concat(concat('%',#{q_productId}),'%') ESCAPE '\')
AND (i.PRODUCT_NAME like concat(concat('%',#{q_productName}),'%') ESCAPE '\')
ORDER BY i.price_config_info_id,i.offer_start_time DESC ) t1) t11 where rn between (#{curPage} - 1) * #{pageSize} + 1 and (#{curPage}) * #{pageSize}

select count(*) from (select * FROM t_migu_price_config_info i where 1=1
and i.project_id = #{projectId,jdbcType=VARCHAR}
AND (i.PRODUCT_ID like concat(concat('%',#{q_productId}),'%') ESCAPE '\')
AND (i.PRODUCT_NAME like concat(concat('%',#{q_productName}),'%') ESCAPE '\')
)

select P.PRICE_CONFIG_INFO_ID,P.PRODUCT_ID,P.PRODUCT_NAME,P.MIN_SELL_PRICE from t_migu_price_config_info p where 1=1
and P.PROJECT_ID = #{projectId}
AND (#{today} between p.offer_start_time and p.offer_end_time)

select * from t_migu_price_config_info m 
where 
m.project_id=#{0}
and #{1} between m.offer_start_time 
and m.offer_end_time 
and m.product_id=#{2}
and m.PRODUCT_NAME=#{3}
order by m.min_sell_price asc
